def Tonelli_Shanks(a, p):

  def isQR(a, p):
    return pow(a, (p - 1) // 2, p) == 1

  if not isQR(a, p):
    return -1

  Q, S = p - 1, 0
  cnt = 0
  while Q % 2 == 0:  # 홀수인 Q 찾기!!!
    cnt += 1
    S += 1
    Q //= 2

  z = None
  for i in range(2, p):
    if not isQR(i, p):
      z = i
      break
  M, c, t, R = S, pow(z, Q, p), pow(a, Q, p), pow(a, (Q + 1) // 2, p)

  while True:
    if t == 0:
      return 0

    if t == 1:
      return R

    for k in range(1, M):
      if (t**(2 * k)) % p == 1:
        i = k
        break
    b = pow(c, 2**(M - i - 1), p) % p
    M = i
    c = (b**2) % p
    t = (t * (b**2)) % p
    R = (R * b) % p


print(Tonelli_Shanks(a, p))